const mobileMenuButton=document.getElementById("mobile-menu-button");const mobileMenu=document.getElementById("mobile-menu");if(mobileMenuButton&&mobileMenu){mobileMenuButton.addEventListener("click",()=>{mobileMenu.classList.toggle("hidden")});mobileMenu.querySelectorAll("a").forEach(link=>{link.addEventListener("click",()=>{mobileMenu.classList.add("hidden")})})}const themeToggle=document.getElementById("theme-toggle");const body=document.body;const currentTheme=localStorage.getItem("theme");if(currentTheme){body.classList.add(currentTheme);if(currentTheme==="light-theme"){themeToggle.querySelector("i").classList.remove("fa-moon");themeToggle.querySelector("i").classList.add("fa-sun")}}else{body.classList.add("dark-theme");localStorage.setItem("theme","dark-theme")}themeToggle.addEventListener("click",()=>{if(body.classList.contains("dark-theme")){body.classList.remove("dark-theme");body.classList.add("light-theme");themeToggle.querySelector("i").classList.remove("fa-moon");themeToggle.querySelector("i").classList.add("fa-sun");localStorage.setItem("theme","light-theme")}else{body.classList.remove("light-theme");body.classList.add("dark-theme");themeToggle.querySelector("i").classList.remove("fa-sun");themeToggle.querySelector("i").classList.add("fa-moon");localStorage.setItem("theme","dark-theme")}});function showModal(message){const modal=document.getElementById("myModal");const modalMessage=document.getElementById("modal-message");modalMessage.textContent=message;modal.style.display="flex"}function closeModal(){const modal=document.getElementById("myModal");if(modal){modal.style.display="none"}}document.addEventListener("DOMContentLoaded",()=>{const myWritingsCards=document.querySelectorAll(".blog-grid > div.blog-post-card");const allCards=document.querySelectorAll(".blog-post-card");const startIndex=allCards.length-3;if(startIndex>=0){for(let i=startIndex;i<allCards.length;i++){const card=allCards[i];if(!card.hasAttribute("data-url")){card.style.cursor="pointer";card.addEventListener("click",()=>{window.open("https://merafe-stories.netlify.app/","_blank")})}}}});document.addEventListener("DOMContentLoaded",()=>{const techBytesCards=document.querySelectorAll(".blog-post-card[data-url]");techBytesCards.forEach(card=>{card.style.cursor="pointer";card.addEventListener("click",event=>{if(!event.target.closest(".blog-post-button")){const url=card.dataset.url;if(url){window.open(url,"_blank")}}})})});