document.addEventListener("DOMContentLoaded",function(){const mobileMenuButton=document.getElementById("mobile-menu-button");const mobileMenu=document.getElementById("mobile-menu");const desktopThemeToggle=document.getElementById("theme-toggle-desktop");const mobileThemeToggle=document.getElementById("theme-toggle-mobile");if(mobileMenuButton&&mobileMenu){mobileMenuButton.addEventListener("click",()=>{mobileMenu.classList.toggle("hidden")});mobileMenu.querySelectorAll("a").forEach(link=>{link.addEventListener("click",()=>{mobileMenu.classList.add("hidden")})})}function applyTheme(theme){if(theme==="light"){document.body.classList.add("light-theme")}else{document.body.classList.remove("light-theme")}updateThemeIcons()}function updateThemeIcons(){const iconClass=document.body.classList.contains("light-theme")?"fa-sun":"fa-moon";const desktopIcon=desktopThemeToggle?desktopThemeToggle.querySelector("i"):null;if(desktopIcon){desktopIcon.classList.remove("fa-sun","fa-moon");desktopIcon.classList.add(iconClass)}const mobileIcon=mobileThemeToggle?mobileThemeToggle.querySelector("i"):null;if(mobileIcon){mobileIcon.classList.remove("fa-sun","fa-moon");mobileIcon.classList.add(iconClass)}}const savedTheme=localStorage.getItem("theme");if(savedTheme){applyTheme(savedTheme)}else{applyTheme("dark")}if(desktopThemeToggle){desktopThemeToggle.addEventListener("click",function(){const currentTheme=document.body.classList.contains("light-theme")?"light":"dark";const newTheme=currentTheme==="light"?"dark":"light";localStorage.setItem("theme",newTheme);applyTheme(newTheme)})}if(mobileThemeToggle){mobileThemeToggle.addEventListener("click",function(){const currentTheme=document.body.classList.contains("light-theme")?"light":"dark";const newTheme=currentTheme==="light"?"dark":"light";localStorage.setItem("theme",newTheme);applyTheme(newTheme)})}function showModal(message){const modal=document.getElementById("myModal");const modalMessage=document.getElementById("modal-message");modalMessage.textContent=message;modal.style.display="flex"}});